import React, {FC, useEffect, useContext} from 'react';
import { Context } from './index';
import LoginForm from './components/LoginForm';

const App:FC = () => {

  const {store} = useContext(Context)
  useEffect(() => {
    if (localStorage.getItem('token')) {
      store.checkAuth()
    }
  }, [])
  return (
    <div className="App">
      <LoginForm />
    </div>
  );
}

export default App;
